return items.map(item => {
  let wf = item.json;

  wf.nodes.forEach(node => {
    // Remove credenciais
    if (node.credentials) {
      node.credentials = null;
    }

    // Remove parâmetros sensíveis
    const paramKeys = ['apiKey', 'accessToken', 'authToken', 'authentication', 'secret', 'token'];
    paramKeys.forEach(key => {
      if (node.parameters?.[key]) {
        node.parameters[key] = '***';
      }
    });

    // Anonimiza headers
    if (node.parameters?.headers) {
      node.parameters.headers = node.parameters.headers.map(h => ({
        name: h.name,
        value: '***'
      }));
    }

    // Limpa opções HTTP
    if (node.type === 'n8n-nodes-base.httpRequest' && node.parameters?.options) {
      node.parameters.options = {};
    }

    // Sanitize URLs com tokens
    if (node.parameters?.url && typeof node.parameters.url === 'string') {
      node.parameters.url = node.parameters.url.replace(/(apiKey|token|secret|accessToken)=.+?(&|$)/gi, '$1=***$2');
    }
  });

  // SANITIZAÇÃO COMPLETA DO JSON COMO STRING
  try {
    let stringified = JSON.stringify(wf);

    // Regex para remover possíveis segredos embutidos (formas seguras!)
    const patterns = [
      /(sk-[a-zA-Z0-9-_]{10,})/g,              // OpenAI keys
      /(GROQ_[a-zA-Z0-9-_]{10,})/g,            // GROQ keys
      /(gh[pousr]_[a-zA-Z0-9-_]{10,})/g,       // GitHub tokens
      /(eyJ[a-zA-Z0-9-_]+\.[a-zA-Z0-9-_]+\.[a-zA-Z0-9-_]+)/g, // JWTs
      /("authorization"\s*:\s*")[^"]+(")/gi,   // Authorization headers
      /("Bearer [^"***8864]+")/gi                     // Bearer tokens
    ];

    patterns.forEach(pattern => {
      stringified = stringified.replace(pattern, (_, match1, match2) => {
        if (match2 !== undefined) {
          return match1 + '***' + match2;
        }
        return '"***"';
      });
    });

    wf = JSON.parse(stringified); // ← Aqui estava dando erro antes
    item.json = wf;
  } catch (error) {
    item.json = { error: `Erro ao sanitizar JSON: ${error.message}` };
  }

  return item;
});
