// Custom UUID generation function
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0,
          v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// Simulated in-memory database for conversation storage
let conversations = $json["conversations"] || {};
const phone = $json["connectedPhone"];
const message = {
  text: $json["messageText"], // Assuming `messageText` contains the new message text
  timestamp: new Date().toISOString()
};

let conversationId = conversations[phone]?.conversationId;
let conversation = conversations[phone]?.conversation || [];

// If no conversation exists, create a new one
if (!conversationId) {
  conversationId = uuidv4();
}

// Append the new message to the conversation
conversation.push(message);

// Store the conversation back in the 'database'
conversations[phone] = { conversationId, conversation };

return [{ ...$json, conversationId, conversation }];

