// Custom UUID generation function
function uuidv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        const r = Math.random() * 16 | 0,
              v = c === 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
}

// Get all input data from n8n
const inputData = $input.all();
const outputData = [];

// Process each input item
for (let item of inputData) {
    // Ensure 'conversations' object exists
    let conversations = item.json?.conversations || {};

    // Extract phone number safely
    const phone = item.json?.Telefone || "Unknown";  // Airtable field "Telefone"

    // Extract the message content safely
    const messageText = item.json?.concat_Messages || "No message provided";  

    // Extract or generate a conversation ID
    let conversationId = uuidv4();

    // Ensure a conversation exists for this phone number
    if (!conversations[phone]) {
        conversations[phone] = { conversationId, conversation: [] };
    }

    // Create message object
    const message = {
        text: messageText,
        timestamp: new Date().toISOString()
    };

    // Append message to conversation
    conversations[phone].conversation.push(message);

    // Push structured output
    outputData.push({
        ...item.json,
        conversationId,
        conversation: conversations[phone].conversation
    });
}

// Return structured data
return outputData;

