// Fun√ß√£o para dividir o texto em mensagens com base em duas quebras de linha consecutivas
function splitTextIntoMessages(text) {
    if (typeof text !== 'string') {
        console.log('Invalid input: text is not a string', text);
        throw new Error('Invalid input: text must be a string');
    }
    return text.split('\n\n').map(part => part.trim()).filter(part => part.length > 0).map(part => {
        return { json: { message: part } };
    });
}

